// Function 13 â€“ Geofence + Persistent Check-IN/OUT Log (no clearing on re-entry)

let payload = msg.payload;
let now = payload.tst || payload.timestamp || Math.floor(Date.now() / 1000);

// Preserve lat/lon
payload.lat = payload.lat || msg.lat;
payload.lon = payload.lon || msg.lon;

// University geofence
const workLat = 41.796583;
const workLon = -6.769472;
const radius = 50; // meters

// Distance helper
function dist(lat1, lon1, lat2, lon2) {
    const R = 6371e3;
    const toRad = v => v * Math.PI / 180;
    const dLat = toRad(lat2 - lat1), dLon = toRad(lon2 - lon1);
    const a = Math.sin(dLat / 2) ** 2 +
        Math.cos(toRad(lat1)) *
        Math.cos(toRad(lat2)) *
        Math.sin(dLon / 2) ** 2;
    return R * 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
}

// Compute current status
let currStatus = dist(payload.lat, payload.lon, workLat, workLon) <= radius
    ? "INSIDE"
    : "OUTSIDE";

// Load previous state from flow context
let state = flow.get("state") || {
    status: null,
    lastCheckIn: null,
    log: []
};

// Only record when status actually changes
if (state.status !== currStatus) {
    if (currStatus === "INSIDE") {
        // Arrived
        state.lastCheckIn = now;
        state.log.push({ event: "Check-IN", time: now });
    }
    else if (currStatus === "OUTSIDE" && state.lastCheckIn) {
        // Left
        state.log.push({ event: "Check-OUT", time: now });
        state.lastCheckIn = null;
    }
}

// Update and persist state
state.status = currStatus;
flow.set("state", state);

// Debug
node.warn(`Status changed â†’ ${currStatus}. Total events: ${state.log.length}`);

// Final payload
msg.payload = {
    currentStatus: currStatus,
    timestamp: now,
    readableTime: new Date(now * 1000).toLocaleString(),
    log: state.log,
    lat: payload.lat,
    lon: payload.lon,
    distanceFromWork: Math.floor(dist(payload.lat, payload.lon, workLat, workLon))
};

return msg;

let payload = msg.payload;

// If no log array, return empty table
if (!payload.log || !Array.isArray(payload.log)) {
    msg.payload = [];
    return msg;
}

let entries = payload.log;
let rows = [];

// Walk through log, pairing each Check-IN with the next Check-OUT
for (let i = 0; i < entries.length; i++) {
    if (entries[i].event === "Check-IN") {
        let inTime = entries[i].time;
        let outTime = null;

        // If the very next entry is a Check-OUT, grab its time
        if (i + 1 < entries.length && entries[i + 1].event === "Check-OUT") {
            outTime = entries[i + 1].time;
        }

        rows.push({
            "Check-IN": new Date(inTime * 1000).toLocaleString(),
            "Check-OUT": outTime
                ? new Date(outTime * 1000).toLocaleString()
                : ""
        });
    }
}

msg.payload = rows;
return msg;

const payload = msg.payload;

