let logs = msg.payload;
let dailyDurations = {};
let insideTime = null;

logs.forEach(entry => {
  let t = new Date(entry._time);
  let ds = t.toISOString().slice(0, 10);

  if (entry._value === "INSIDE") {
    insideTime = t;
  }
  else if (entry._value === "OUTSIDE" && insideTime) {
    let mins = Math.round((t - insideTime) / 60000);
    if (mins > 0 && mins < 12 * 60) {
      dailyDurations[ds] = (dailyDurations[ds] || 0) + mins;
    }
    insideTime = null;
  }
});

// Agar last event INSIDE hai to uska duration aaj tak add karo
if (insideTime) {
  let now = new Date();
  let insideDate = insideTime.toISOString().slice(0, 10);
  let todayStr = now.toISOString().slice(0, 10);

  // Agar INSIDE event ka date aaj ke barabar hai, to pura duration add karo
  // Agar INSIDE time kisi purane date ka hai aur abhi tak OUTSIDE nahi aaya, us case me us date ke liye duration calculate karna thoda complex hota hai (split karna pade)
  if (insideDate === todayStr) {
    let liveMins = Math.round((now - insideTime) / 60000);
    if (liveMins > 0 && liveMins < 12 * 60) {
      dailyDurations[todayStr] = (dailyDurations[todayStr] || 0) + liveMins;
    }
  } else {
    // Optional: agar INSIDE time purani date ka hai aur OUTSIDE nahi aya, 
    // toh wo purane din ke liye poora 12 ghante ya ab tak ka duration add karne ka logic yahan bana sakte ho
  }
}

msg.payload = dailyDurations;
return msg;

let durations = msg.payload;   // { "2025-05-17":18, "2025-05-19":45, "2025-05-22":3 }
let today = new Date();
let labels = [];
let values = [];

for (let i = 6; i >= 0; i--) {
    let d = new Date(today);
    d.setDate(today.getDate() - i);
    let ds = d.toISOString().slice(0, 10);  // "YYYY-MM-DD"

    labels.push(ds);

    // convert minutes to hours (rounded to 2 decimals)
    let mins = durations[ds] || 0;
    let hrs = Math.round((mins / 60) * 100) / 100;
    values.push(hrs);
}

msg.payload = [{
    series: ["Work Duration (h)"],
    data: [values],
    labels: labels
}];
return msg;

let data = msg.payload;

