msg.measurement = "attendance";

return msg;

const durations = global.get("workDurations") || {};
const today = new Date().toISOString().slice(0, 10);
const durationToday = durations[today] || 0;

const expected = 8; // Expected hours

if (durationToday < expected) {
    msg.payload = `Worked less than expected (${durationToday}h < ${expected}h)`;
} else if (durationToday > expected) {
    msg.payload = `Worked more than expected (${durationToday}h > ${expected}h)`;
} else {
    msg.payload = `Worked as expected (${durationToday}h)`;
}
return msg;

let totalInsideDurationMs = 0;
let lastInsideTimestamp = null;

for (let i = 0; i < msg.payload.length; i++) {
    let event = msg.payload[i];
    let eventTime = new Date(event._time).getTime();
    let status = event._value;  // INSIDE ya OUTSIDE

    if (status === "INSIDE") {
        if (lastInsideTimestamp === null) {
            // INSIDE period start
            lastInsideTimestamp = eventTime;
        }
    } else {
        // Status OUTSIDE, INSIDE period khatam
        if (lastInsideTimestamp !== null) {
            totalInsideDurationMs += (eventTime - lastInsideTimestamp);
            lastInsideTimestamp = null;
        }
    }
}

// Agar abhi bhi INSIDE hai (last status INSIDE tha)
if (lastInsideTimestamp !== null) {
    totalInsideDurationMs += (Date.now() - lastInsideTimestamp);
}

// Convert to minutes
let totalInsideMinutes = Math.floor(totalInsideDurationMs / 60000);

msg.payload = totalInsideMinutes;
return msg;

